/**
 * Test class for GTW_Model_User.
 * Generated by PHPUnit on 2011-08-08 at 13:25:47.
 */
class GTW_Model_CouponTest extends PHPUnit_Framework_TestCase {

	/**
	 * @var GTW_Model_Coupon
	 */
	protected $object;


	public function testSetUser() {
		$userData = array(
			'first_name'	=> 'Sean',
			'last_name'		=> 'Thayne',
			'email'			=> 'sean@skyseek.com',
			'password'		=> md5('test123'),
			'role'			=> 'guest'
		);

		$user = new GTW_Model_User($userData);

		$this->object->user = $user;
		
		$this->assertEquals($user, $this->object->user);
	}

	public function testInvalidUser() {
		try {
			$this->object->user = "Steve";
		} catch (Exception $e) {
			return true;
		}

		$this->fail("Was able to set a invalid property type");
	}

	/**
	 * Sets up the fixture, for example, opens a network connection.
	 * This method is called before a test is executed.
	 */
	protected function setUp() {
		$this->object = new GTW_Model_Coupon;
	}

	/**
	 * Tears down the fixture, for example, closes a network connection.
	 * This method is called after a test is executed.
	 */
	protected function tearDown() {
		
	}

}

